url_rewrite_root:
  priority is given to the url higher in the list
  
    
    
      PageHandlersLoader.aspx?
      /PageHandlersLoader.aspx$_asp query_string;
    

    
      /page_content/public_user_info/id
      /?page_content=$_asp section_2;&user_id=$_asp section_4;$_asp query_string;
    

    
      /page_content/public_user_info/name
      /?page_content=$_asp section_2;&user_site_name=$_asp section_4;$_asp query_string;
    

    
      /page_content
      /?page_content=$_asp section_2;$_asp query_string;
    
  
    
      ^/content/([0-9]+)
      /?page_content=content&content_id=$_asp section_2;&title=$_asp section_3;
    
    
    
      ^/content_type/([_0-9a-z-]+)
      /?page_content=main&content_type=$_asp section_2;
    

    
      ^/tag/([\p{L}0-9_\s]+)
      /?page_content=main&tag=$_asp section_2;
    

    
      ^/category/([_0-9a-z-]+)/content_page/([0-9]+)
      /?page_content=main&category=$_asp section_2;&page=$_asp section_4;
    

    
      ^/category/([_0-9a-z-]+)
      /?page_content=main&category=$_asp section_2;
    

    
      ^/site/([_0-9a-z-]+)
      /?site=$_asp section_2;
    
    
    
      ^/lang/([_a-z-]+)
      /?language=$_asp section_2;
    
   
    
      ^/content_page/([0-9]+)
      /?page_content=main&page=$_asp section_2;
    

    
      ^/page/search/content_page/([0-9]+)
      /page/search/?page=$_asp section_4;
    

    
      ^/page/print/([0-9]+)
      /page/print/?content_id=$_asp section_3;
    
    
    
      ^/action/text_creator/([_a-z-]+)
      /action/text_creator/?value=$_asp section_3;$_asp query_string;
    
    
    
      ^/action/new_password/
      /action/new_password/?user_id=$_asp section_3;&value=$_asp section_4;
    
    
    
      ^/page/error/([_0-9a-z-]+)
      /page/error/?value=$_asp section_3;
    
    
    
      ^/rss/category/([_0-9a-z-]+)
      /rss/?category_name=$_asp section_3;
    
    
    
      ^/rss/content_type/([_0-9a-z-]+)
      /rss/?content_type=$_asp section_3;
    
    
    
      ^/rss/site/([_0-9a-z-]+)
      /rss/?site=$_asp section_3;
    
    
    
      ^/rss/user/([0-9]+)
      /rss/?user_id=$_asp section_3;
    
    
    
      ^/sitemap/site/([_0-9a-z-]+)
      /sitemap/?site=$_asp section_3;
