(function() {
    tinymce.PluginManager.add('readmore', function( editor, url ) {
        editor.addButton( 'readmore', {
            icon: 'readmore',
			title: 'Read more',
            onclick: function() {
                editor.insertContent("<hr class=\"el_read_more\">");
            }
        }),        editor.addMenuItem( 'readmore', {
            text: 'Read more',
            icon: 'readmore',
			context:"insert",
            onclick: function() {
                editor.insertContent("<hr class=\"el_read_more\">");
            }
        });
    });
})();